mapper.select:
select count(*) 
from shopowner
where soId=#{soId}

select count(*) 
from shopowner
where soLicense=#{soLicense}

 	select soId,soPasswd,soName,soPhone,soLicense,soPost,soAddr,soLevel,to_char(soJoindate,'YYYY-MM-DD') as soJoindate
		from shopowner
		where soId = #{soId} and soPasswd=#{soPasswd}

 	select soId,soPasswd,soName,soPhone,soLicense,soPost,soAddr,soLevel,to_char(soJoindate,'YYYY-MM-DD') as soJoindate
		from shopowner
		where soId = #{soId}

 	select soId
		from shopowner
		where soLicense = #{soLicense} and soName = #{soName} and soPhone = #{soPhone}

 	select soPasswd
		from shopowner
		where soId = #{soId} and soLicense = #{soLicense} and soName = #{soName} and soPhone = #{soPhone}

mapper.update:
update shopowner
set soPasswd=#{soPasswd}
where soId=#{soId}

mapper.select:
select count(*) 
from shopowner
where soId=#{soId} and soPasswd=#{soPasswd}

mapper.update:
update shopowner
set soPhone=#{soPhone}
where soId=#{soId}

mapper.select:
  		select count(*) 
		from shop
		where soId = #{soId}

  		select count(*) 
		from review
		where sCode = #{sCode}

  		select count(*)
		from interest
		where sCode = #{sCode}

  		select sum(couponPrice)
		from coupon
		where sCode=#{sCode}

  		select (payDate+period) as expireDate 
  		from payment
		where soId = #{soId}

  		select to_char((soJoindate+90),'YYYY-MM-DD') as FreetrialDate 
  		from shopowner
		where soId = #{soId}
