mapper.select:
     select count(*)
     from soqna
	 where qnaComplete=#{searchName}

mapper.update:
update soqna
set qnaTitle=#{qnaTitle}, qnaContent=#{qnaContent}
where soId=#{soId} and qnaNum=#{qnaNum}

update soqna
set qnaComplete='처리중'
where qnaNum=#{qnaNum}

update soqna
set qnaComplete='처리완료'
where qnaNum=#{qnaNum}

update soqnareply
set qnaReplyContent=#{qnaReplyContent}
where qnaReplyNum=#{qnaReplyNum}
